import java.util.*;public class Main{    public static void main(String[] args){        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();                while(n-->0){            int s = sc.nextInt();            int[] arr = new int[s];            int ope = 0;            int neg = 0;            int zero = 0;            for(int i=0;i<arr.length;i++){                arr[i] = sc.nextInt();                if(arr[i] == -1) neg++;                if(arr[i] == 0) zero++;              }            if(neg%2 != 0){                ope+=2;            }            while(zero-->0) ope++;            System.out.println(ope);        }    }}