import java.util.*;public class Main{    public static void main(String[] args){        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t-->0){            int n = sc.nextInt();            int k = sc.nextInt();                                        String s = sc.next();                                        int count = 0;                for(int i=0;i<n;i++){                    if(s.charAt(i) == '1'){                        int yes = 1;                        for(int j=Math.max(0,i-k+1);j<i;j++){                            if(s.charAt(j) == '1') {                                                                yes = 0;                                break;                            }                        }                        if(yes == 1) {                            count++;                            i+=k-1;                        }                    }                }                System.out.println(count);            }                                }    }